CREATE TABLE "public"."chat" ("id" uuid NOT NULL, "sender" integer NOT NULL, "receiver" integer NOT NULL, "message" Text NOT NULL DEFAULT ' ', "time" timestamptz NOT NULL DEFAULT now(), PRIMARY KEY ("id") , FOREIGN KEY ("sender") REFERENCES "public"."user"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("receiver") REFERENCES "public"."user"("id") ON UPDATE restrict ON DELETE restrict, UNIQUE ("id"));
